QueryDefinition.Query:
DECLARE @sql nvarchar(MAX) DECLARE @value varchar(MAX)
if exists(select * from sys.columns where Name = N'RXMR' and Object_ID = Object_ID(N'DRUG_CLAIMS')) begin SET @value = 'RXMR,' end else begin SET @value = '1 RXMR,' end;
SET @sql = ' with a as ( SELECT z.ENROLID AS PERSON_ID, z.SVCDATE AS DRUG_EXPOSURE_START_DATE, CASE WHEN MAX(z.RXMR) = 2 THEN ''38000176'' ELSE ''38000175'' END AS DRUG_TYPE_CONCEPT_ID, NULL AS STOP_REASON, MAX(z.REFILL) AS REFILLS, ROUND(SUM(z.METQTY),0) AS QUANTITY, CASE WHEN SUM(z.DAYSUPP) > 365 THEN 365 WHEN SUM(z.DAYSUPP) < 0 THEN 0 WHEN SUM(z.DAYSUPP) IS NULL THEN 0 ELSE SUM(z.DAYSUPP) END AS DAYS_SUPPLY, NULL AS SIG, NULL AS PRESCRIBING_PROVIDER_ID, NULL AS VISIT_OCCURRENCE_ID, NULL AS RELEVANT_CONDITION_CONCEPT_ID, z.NDCNUM AS DRUG_SOURCE_VALUE, SUM(z.COPAY) AS PAID_COPAY, SUM(z.COINS) AS PAID_COINSURANCE, SUM(z.DEDUCT) AS PAID_TOWARD_DEDUCTIBLE, SUM(z.NETPAY) AS PAID_BY_PAYER, SUM(z.COB) AS PAID_BY_COORDINATION_BENEFITS, SUM(z.INGCOST) AS INGREDIENT_COST, SUM(z.DISPFEE) AS DISPENSING_FEE, SUM(z.AWP) AS AVERAGE_WHOLESALE_PRICE, SUM(z.COPAY+z.COINS+z.DEDUCT) AS TOTAL_OUT_OF_POCKET, SUM(z.COPAY+z.COINS+z.DEDUCT+z.NETPAY) AS TOTAL_PAID FROM ( SELECT DISTINCT r.ENROLID, r.NDCNUM, r.SVCDATE,' + @value + 'r.METQTY, r.DAYSUPP, r.COPAY, r.COINS, r.DEDUCT, r.NETPAY, r.COB, r.INGCOST, r.DISPFEE, r.AWP, r.REFILL FROM DRUG_CLAIMS r JOIN _chunks ch ON ch.ChunkId = {0} AND r.ENROLID = ch.PERSON_ID )z GROUP BY z.ENROLID, z.NDCNUM,z.NDCNUM, z.SVCDATE)
select *, SUBSTRING(DRUG_SOURCE_VALUE,1,9) DRUG_SOURCE_VALUE_9 from a ORDER BY PERSON_ID '
EXECUTE sp_executesql @sql;
